// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './Components/Navbar';
import Home from './Components/HomePage';
import Reviews from './Components/ReviewPage';
import Login from './Components/LoginPage';
import UnapprovedReviews from './Components/UnapprovedReviews';
import axios from 'axios';

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axios.get('http://localhost:5000/verify-token', {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then(() => setIsAuthenticated(true))
            .catch(() => setIsAuthenticated(false));
        }
    }, []);

    return (
        <Router>
            <Navbar isAuthenticated={isAuthenticated} />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/reviews" element={<Reviews />} />
                <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
                <Route path="/unapproved-reviews" element={isAuthenticated ? <UnapprovedReviews /> : <Navigate to="/login" />} />
                <Route path="/logout" element={<Logout setIsAuthenticated={setIsAuthenticated} />} />
            </Routes>
        </Router>
    );
};

const Logout = ({ setIsAuthenticated }) => {
    useEffect(() => {
        localStorage.removeItem('token');
        setIsAuthenticated(false);
    }, [setIsAuthenticated]);

    return <Navigate to="/" />;
};

export default App;
